import { darken, Grid, Paper, Typography } from '@mui/material';
import { makeStyles } from '@mui/styles';
import React from 'react';
import ReactPlayer from 'react-player';

const useStyles = makeStyles((theme: any) => ({
  root: {
    flexGrow: 1,
    backgroundColor: darken(theme.palette.background.default, 0.8),
    color: theme.palette.primary.contrastText,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  image: {
    width: '100%',
    height: 'auto',
  },
  video: {
    width: '100%',
    height: 'auto',
  },
}));
const More = () => {
  const classes = useStyles();
  const [items, setItems] = React.useState([
    {
      type: 'image',
      url: '../assets/images/sp-app-icon.png',
      title: 'sp logo',
      description: 'lovin social passport',
      id: 1,
    },
    {
      type: 'image',
      url: '../assets/images/sp-app-icon.png',
      title: 'sp logo',
      description: 'lovin social passport',
      id: 1,
    },
    {
      type: 'image',
      url: '../assets/images/sp-app-icon.png',
      title: 'sp logo',
      description: 'lovin social passport',
      id: 1,
    },
    {
      type: 'image',
      url: '../assets/images/sp-app-icon.png',
      title: 'sp logo',
      description: 'lovin social passport',
      id: 1,
    },
    {
      type: 'image',
      url: '../assets/images/sp-app-icon.png',
      title: 'sp logo',
      description: 'lovin social passport',
      id: 1,
    },
    {
      type: 'image',
      url: '../assets/images/sp-app-icon.png',
      title: 'sp logo',
      description: 'lovin social passport',
      id: 1,
    },
  ]);

  React.useEffect(() => {
    // Fetch list of items from API or local data source
    async function fetchData() {
      const response = await fetch('/api/items');
      const data = await response.json();
      setItems(data);
    }
    fetchData();
  }, []);

  return (
    <div className={classes.root}>
      <Grid container spacing={3}>
        {items.map((item) => (
          <Grid item xs={12} sm={6} md={4} key={item.id}>
            <Paper className={classes.paper}>
              {item.type === 'image' ? (
                <img src={item.url} className={classes.image} />
              ) : (
                <ReactPlayer url={item.url} className={classes.video} />
              )}
              <Typography variant="h6">{item.title}</Typography>
              <Typography variant="body2">{item.description}</Typography>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </div>
  );
};

export default More;
